import React, { useState, useEffect } from 'react';
import Logo from '../assets/images/Logo';
import Input from '../assets/components/Input';
import { ReactComponent as Password } from '../assets/images/password.svg';
import { ReactComponent as Username } from '../assets/images/username.svg';

const Login = () => {
  const [data, setData] = useState({});

  useEffect(() => {
    (async () => {
      const response = await fetch(
        "https://opentdb.com/api.php?amount=10"
      );
      const parsed = await response.json();
      setData(parsed);
    })();
  }, []);
  return (
    <div>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  )
}

export default function SplashScreen() {

  return (

    <>
      <div className='text-center py-5 my-5'>
        <Logo height="290"/>
        <form action="/action_page.php" className='vertical-align-middle'>
          <Input type='text' id="fname" placeholder='Username' image={<Username />}/>
          <Input type='text' id="lname" placeholder='Password' image={<Password />}/>
          <Password />
          <input type="text" id="lname" name="lname" placeholder='Password' className='inputText' /><br />
          <input type="submit" value="Submit" />
        </form>

      </div>

      <style>
        {`
    .full {
      height: 100%;
      display: flex;
    }
    .row {
      
    }

   

    ::placeholder {
        color: white;
        opacity: 1;
        font-family: 'Inter', sans-serif;
        font-weight: 700;
    }

    

    .gameAnimation {
      animation-name: left;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    .fragmerAnimation {
      animation-name: right;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    @keyframes logo {
      0% {opacity: 0}
      50% { transform: rotate(0deg); opacity: 1}
      100% {transform: rotate(360deg); opacity: 0}
    }

    @keyframes left {
      from {transform: translate(0px)}
      to {transform: translate(-240px)}
    }

    @keyframes right {
      from {transform: translate(0px)}
      to {transform: translate(240px)}
    }
    `}

      </style>
    </>


  );

};
