import React from 'react';
import LogoTop from '../assets/components/LogoTop';

import { ReactComponent as ArtCulture } from '../images/categories/artculture.svg';
import { ReactComponent as Entertainment } from '../assets/images/';
import { ReactComponent as Geography } from '../images/categories/geography.svg';
import { ReactComponent as Music } from '../images/categories/music.svg';
import { ReactComponent as Science } from '../images/categories/science.svg';
import { ReactComponent as Sports } from '../images/categories/sports.svg';


function OneCateg(props) {
    return (
        <div className='grid '>
            {props.image}
            <p className='text-lg'>{props.label}</p>
        </div>

    )
}

export default function Category() {

    return (
        <>
            <LogoTop />
            <div className='py-20 text-center'>
                <h2>Choose a Category</h2>
            </div>
            <div className="container mx-auto py-20 ">
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-3 gap-9">
                    <OneCateg image={<Entertainment />} label="Entertainment" />
                </div>
            </div>
        </>

    );

};
