import React from 'react';
import Logo from '../assets/Logo';
import { ReactComponent as Username } from '../assets/username.svg'
import { ReactComponent as Password } from '../assets/password.svg'
import { response } from 'express';

const Login = () => {
  const [data, setData] = useState({});

  useEffect(() => {
    (async () => {
      const response = await fetch(
        "https://raw.githubusercontent.com/Shopify/shopify-app-node/master/package.json"
      );
      const parsed = await response.json();
      setData(parsed);
    })();
  }, []);
  return (
    <div>
      <pre>{JSON.stringify(data, null, 2)}</pre>
    </div>
  )
}

export default function SplashScreen() {

  return (

    <>
       <Login />
      <Logo height="290" />
      <form action="/action_page.php">
        <Username />
        <input type="text" id="fname" name="fname" placeholder='Username' className='inputText' /><br />
        <Password />
        <input type="text" id="lname" name="lname" placeholder='Password' className='inputText' /><br />
        <input type="submit" value="Submit" />
      </form>



      <style>
        {`
    .full {
      height: 100%;
      display: flex;
    }
    .row {
      
    }

    .inputText {
      color: white;
      font-size: 20px;
      background-color: transparent;
      border-width: 0px;
      border-color: white;
      border-bottom-width: 1px;
      padding-left: 10px;
    }

    ::placeholder {
        color: white;
        opacity: 1;
    }

    .gameAnimation {
      animation-name: left;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    .fragmerAnimation {
      animation-name: right;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    @keyframes logo {
      0% {opacity: 0}
      50% { transform: rotate(0deg); opacity: 1}
      100% {transform: rotate(360deg); opacity: 0}
    }

    @keyframes left {
      from {transform: translate(0px)}
      to {transform: translate(-240px)}
    }

    @keyframes right {
      from {transform: translate(0px)}
      to {transform: translate(240px)}
    }
    `}

      </style>
    </>


  );

};
