import React from 'react';
import Logo from '../assets/Logo';


export default function SplashScreen() {

    return (

        <>
            <form action="/action_page.php">
                <label for="fname">First name:</label>
                <input type="text" id="fname" name="fname" placeholder='Username' className='inputText' /><br />
                <label for="lname">Last name:</label>
                <input type="text" id="lname" name="lname" className='inputText' /><br />
                <input type="submit" value="Submit" />
            </form>



            <style>
                {`
    .full {
      height: 100%;
      display: flex;
    }
    .row {
      
    }
    svg {
      animation-name: logo;
      animation-duration: 6s;
      animation-fill-mode: both;
    }

    .inputText {
      color: white;
      font-size: 20px;
      background-color: transparent;
      border-width: 0px
      border-bottom-width
    }

    ::placeholder {
        color: white;
        opacity: 1;
    }

    .gameAnimation {
      animation-name: left;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    .fragmerAnimation {
      animation-name: right;
      animation-duration: 4s;
      animation-delay: 3s;
      animation-timing-function: ease-in-out;
      animation-fill-mode: both;
    }

    @keyframes logo {
      0% {opacity: 0}
      50% { transform: rotate(0deg); opacity: 1}
      100% {transform: rotate(360deg); opacity: 0}
    }

    @keyframes left {
      from {transform: translate(0px)}
      to {transform: translate(-240px)}
    }

    @keyframes right {
      from {transform: translate(0px)}
      to {transform: translate(240px)}
    }
    `}

            </style>
        </>


    );

};
