import React from 'react';
import LogoTop from '../assets/components/LogoTop';

import { ReactComponent as Leave } from '../assets/images/leave.svg'
import { ReactComponent as ArtCulture } from '../assets/images/categories/artculture.svg';
import { ReactComponent as Entertainment } from '../assets/images/categories/entertainment.svg';
import { ReactComponent as Geography } from '../assets/images/categories/geography.svg';
import { ReactComponent as Music } from '../assets/images/categories/music.svg';
import { ReactComponent as Science } from '../assets/images/categories/science.svg';
import { ReactComponent as Sports } from '../assets/images/categories/sports.svg';


function LeaveButton() {
    return (
        <button className=' absolute left-0 top-0 p-9 hover:text-red-600'>
            <div className='flex items-center gap-2'>
                <Leave />
                <span className='text-2xl font-bold'>Leave</span>
            </div>

        </button>
    )
}

function OneQuestion(props) {
    return (
        <button>
            <div className='grid items-center text-center h-24 bg-white rounded-full'>
                <span className=' text-xl font-bold text-black'>{props.label}</span>
                <div className='absolute flex bg-[#DDDDDD] h-28 w-28 items-center justify-center rounded-full text-5xl font-bold text-[#696969]'>
                    A
                </div>



            </div>
        </button>

    )
}

function OneAnswer(props) {
    return (
        <button>
            <div className='grid items-center text-center h-24 bg-white rounded-full'>
                <span className=' text-xl font-bold text-black'>{props.label}</span>
                <div className='absolute flex bg-[#DDDDDD] h-28 w-28 items-center justify-center rounded-full text-5xl font-bold text-[#696969]'>
                    A
                </div>



            </div>
        </button>

    )
}

export default function Category() {

    return (
        <>
            <LeaveButton />
            <LogoTop />
            <div className='pt-20 pb-10 text-center'>
                <p className='text-2xl font-bold'>Tempo Restante</p>
                <p className='text-5xl font-bold'>0:16</p>
            </div>
            <div className="container grid mx-auto ">
            <OneQuestion image={<Entertainment height="180px" />} label="Entertainment" />
                <div className="grid mx-32 grid-cols-1 md:grid-cols-2 lg:grid-cols-2 xl:grid-cols-2 gap-16">
                    <OneAnswer label="Entertainment" />
                    <OneAnswer label="Science" />
                    <OneAnswer label="Geography" />
                    <OneAnswer label="Art & Culture" />
                </div>
            </div>
        </>

    );

};
