import React from 'react';
import LogoTop from '../assets/components/LogoTop';
import { ReactComponent as Loader } from '../assets/images/loader.svg'

export default function RoomGame(props) {

    return (
        <>
            <LogoTop />
            <div className=" grid h-full ">
                <div className="grid md:grid-cols-2 my-auto">
                    <div className=' grid text-center items-center justify-items-center gap-5'>
                        {props.host
                            ? (<button className="bg-white font-extrabold text-xl text-[#1B48E9] hover:opacity-90 rounded w-1/3 h-full justify-self-center my-3 disabled:opacity-60 " >START</button>)
                            : (<>
                                <p className='text-4xl font-bold'>Waiting for host to start...</p>
                                <Loader className='animate-spin my-4' />
                            </>)}
                            <button className="bg-white font-extrabold text-xl text-[#E91B1B] hover:opacity-90 rounded w-1/3 h-full justify-self-center my-3 disabled:opacity-60" >LEAVE ROOM</button>
                    </div>
                    <div>
                        <p className='text-2xl font-bold'>Players in the room:</p>
                        <ul>
                            <li>sporting2003</li>
                            <li>otorrinolaringologista77</li>
                        </ul>
                    </div>

                </div>
            </div>
        </>

    );

};
